const modal=document.getElementById("modal"),overlay=document.getElementById("overlay"),createButton=document.getElementById("create__btn"),closeButton=document.getElementById("close");createButton.addEventListener("click",(()=>{modal.classList.add("show"),overlay.classList.add("show")})),closeButton.addEventListener("click",(()=>{modal.classList.remove("show"),overlay.classList.remove("show")})),overlay.addEventListener("click",(()=>{modal.classList.remove("show"),overlay.classList.remove("show")}));