function data(){document.getElementById("form-input-password")}function StrengthChecker(e){data(),strongPassword.test(e)?password.style.borderColor="green":mediumPassword.test(e)?password.style.borderColor="orange":e.length<1?password.style.borderColor="transparent":password.style.borderColor="red"}password.addEventListener("input",(()=>{clearTimeout(timeout),timeout=setTimeout((()=>StrengthChecker(password.value)),500)}));