class createStudentNoti{constructor(t){this.notiElement=t,this.createNoti()}createNoti(){let t=this;$(this.notiElement).click((function(e){e.preventDefault();let n=this;$.ajax({type:"GET",url:$(n).attr("href"),success:function(e){console.log("Pinned Successfully");let o=document.getElementById($(n).attr("href")+"-title").value,i=document.getElementById($(n).attr("href")+"-file").value,l=e.newNotiId,s=t.newNotiDom(l,o,i);$(".save-cont").prepend(s),new Noty({theme:"relax",text:"Noti Pinned!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){console.log(t.responseText),new Noty({theme:"relax",text:"Error in pinning Noti!",type:"error",layout:"topRight",timeout:1500}).show()}})}))}newNotiDom(t,e,n){return $(`\n    <div class="notification">\n    <div class="text">\n      <p class="old">${e}</p>\n    </div>\n    <div class="pdf">\n    <a href="<%=notidata[i].notiflie%>" target="_blank">\n      <svg xmlns="http://www.w3.org/2000/svg" enable-background="new 0 0 24 24"  viewBox="0 0 24 24" ><g><rect/></g><g><path d="M5,20h14v-2H5V20z M19,9h-4V3H9v6H5l7,7L19,9z"/></g></svg>\n    </a><a class="createStudentNotiBtn" href="/student/unpinnoti/<%=notidata[i]._id%>">\n    <svg xmlns="http://www.w3.org/2000/svg"  viewBox="0 0 24 24"><path d="M0 0h24v24H0V0z" fill="none"/><path d="M17 3H7c-1.1 0-2 .9-2 2v16l7-3 7 3V5c0-1.1-.9-2-2-2z"/></svg>\n    </a>\n  </div>\n  </div>\n        `)}}function notificationSaved(){"use strict";const t=document.querySelector(".saved"),e=document.querySelector(".noted"),n=document.querySelector(".noty1"),o=document.querySelector(".noty2");t.addEventListener("click",(function(){n.style.display="none",o.style.display="block"})),e.addEventListener("click",(function(){n.style.display="block",o.style.display="none"}))}notificationSaved();