function start(){"use strict";const e=document.querySelector(".select-menu"),t=e.querySelector(".select-btn"),c=e.querySelectorAll(".option"),r=e.querySelector(".sBtn-text");t.addEventListener("click",(()=>e.classList.toggle("active"))),c.forEach((t=>{t.addEventListener("click",(()=>{let c=t.querySelector(".option-text").innerText;r.innerText=c,e.classList.remove("active")}))}))}start();