let createform=function(){const e=$("#att-form");$(document).on("submit","#att-form",(function(t){t.preventDefault(),$.ajax({type:"post",url:"/teacher/addattendance",data:e.serialize(),success:function(e){newattDom(e.data);new Noty({theme:"relax",text:"Attendance added",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))},newattDom=function(e){date.value=null,console.log(e);for(let t=0;t<e.student.length;t++){console.log(e.student.length);document.getElementById("len"+t).innerHTML=e.student[t].present.length,document.getElementById("tp"+t).innerHTML=e.student[t].totalpresent,document.getElementById("per"+t).innerHTML=parseInt(e.student[t].totalpresent/e.student[t].present.length*100)+"%",document.getElementById("cb"+t).checked=!0}};