class updateInternalMarks{constructor(){this.MarksForm=$("#marks-all"),this.sendMarksData()}sendMarksData(){this.MarksForm.submit((function(t){t.preventDefault();$.ajax({type:"post",url:"/teacher/updateMarks",data:$(this).serialize(),success:function(){new Noty({theme:"relax",text:"Marks Updated",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){new Noty({theme:"relax",text:"Error in updating marks",type:"error",layout:"topRight",timeout:1500}).show()}})}))}}